import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { Button, Avatar, Typography, Container } from '@mui/material';
import axios from 'axios'; // To fetch profile data
import { GoogleLogout } from 'react-google-login'; // GoogleLogout for logout functionality
import { fetchProfile } from './actions'; // Assuming fetchProfile is the action to load the profile
import Nav from '../Nav';
import './profile.css';

const Profile = () => {
  const profile = useSelector((state) => state.profile); // Fetch profile data from Redux
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Load profile data on component mount
  useEffect(() => {
    // Dispatch an action to fetch the profile
    dispatch(fetchProfile());
  }, [dispatch]);

  // Logout function
  const logOut = () => {
    dispatch({ type: 'LOGOUT' }); // Dispatch an action to clear the profile data
    navigate('/login'); // Redirect to login page after logout
  };

  return (
    <>
      <Nav /> {/* Navigation bar */}
      <Container maxWidth="sm" style={{ textAlign: 'center', marginTop: '50px' }}>
        {profile.name ? (
          <div>
            <Typography variant="h3" gutterBottom>Profile</Typography>
            <Avatar src={profile.imageUrl} alt={profile.name} sx={{ width: 150, height: 150, margin: '20px auto' }} />
            <Typography variant="h5">Name: {profile.name}</Typography>
            <Typography variant="h6">Email: {profile.email}</Typography>
            <Typography variant="h6">Career: {profile.career}</Typography> {/* Display Career */}
            <Typography variant="h6">Year: {profile.year}</Typography> {/* Display Year */}
            <Button
              variant="contained"
              color="primary"
              sx={{ marginTop: 3 }}
              onClick={logOut}
            >
              Log out
            </Button>
          </div>
        ) : (
          <Typography variant="h6">No user logged in</Typography>
        )}
      </Container>
    </>
  );
};

export default Profile;
