import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Nav from '../Nav';
import { useSelector } from 'react-redux';
import { Line } from 'react-chartjs-2'; // We will use Line chart for both datasets
import 'chart.js/auto'; // Import Chart.js
import "./Course_2.css";

function Course_2() {
  const [year, setYear] = useState(1);
  const [curriculum, setCurriculum] = useState(null);
  const [isReturningUser, setIsReturningUser] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [Raring, setRatings] = useState({});
  const [career, setCareer] = useState(''); // Hold the career from backend
  const [careerRequirement, setCareerRequirement] = useState(3.5); // Default requirement for Software Engineer

  const profile = useSelector(state => state.profile);

  useEffect(() => {
    if (profile && profile.email) {
      axios.get(`http://localhost:8000/api/user/${encodeURIComponent(profile.email)}`)
        .then(res => {
          setCurriculum(res.data.curriculum);
          setYear(res.data.user.year);
          setCareer(res.data.user.career || "No career selected");
          setIsReturningUser(true);

          if (res.data.user && res.data.user.grades) {
            const updatedGrades = {};
            res.data.curriculum.subjects.forEach((subject) => {
              updatedGrades[subject.name] = res.data.user.grades[subject.name] || '';
            });
            setGrades(updatedGrades);
          }
          setIsLoading(false);
        })
        .catch(err => {
          console.error('Error fetching user data:', err);
          setIsReturningUser(false);
          setIsLoading(false);
        });
    } else {
      setIsLoading(false);
    }
  }, [profile]);

  // Predefined career requirements
  useEffect(() => {
    const careerRequirements = {
      "Data Analysis": 3.75,
      "Software Engineer": 3.5,
      "Web Development": 3.5,
      "Other": 3.0
    };

    setCareerRequirement(careerRequirements[career] || 3.0);
  }, [career]);

  const handleSubjectClick = (subject) => {
    if (selectedSubject === subject) {
      setShowPopup(false);
      setSelectedSubject(null);
    } else {
      setSelectedSubject(subject);
      setShowPopup(true);
    }
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    setSelectedSubject(null);
  };

  const handleRatingChange = (subjectName, value) => {
    setRatings({ ...ratings, [subjectName]: value });
  
    // Save the updated rating to the backend
    const updatedRating = parseFloat(value);
    if (updatedRating >= 0 && updatedRating <= 10) {  // Assuming a rating scale of 0-10
      axios.post('http://localhost:8000/api/user/rating', {
        email: profile.email,    // Add user email for identification
        subject: subjectName,    // The subject the rating corresponds to
        rating: updatedRating    // The new rating value
      }).then(response => {
        console.log('Rating saved successfully:', response.data);
      }).catch(error => {
        console.error('Error saving rating:', error);
      });
    } else {
      console.error('Invalid rating input. Please enter a rating between 0 and 10.');
    }
  };
  
  // Calculate predicted rating for missing subjects
  const calculatePredictedRating = () => {
    const requiredAverage = careerRequirement; // Assume this is based on a scale from 0-10
    let enteredRatings = 0, enteredTotal = 0;
  
    curriculum.subjects.forEach((subject) => {
      const rating = parseFloat(ratings[subject.name]);
      if (rating) {
        enteredRatings += 1;
        enteredTotal += rating;
      }
    });
  
    const remainingSubjects = curriculum.subjects.length - enteredRatings;
    if (remainingSubjects === 0) {
      return 0; // All ratings entered, no prediction needed
    }
  
    const predictedRating = (requiredAverage * curriculum.subjects.length - enteredTotal) / remainingSubjects;
    return predictedRating;
  };
  
  // Check if achieving the required average rating is possible
  const isPossibleToAchieveRequirement = () => {
    const predictedRating = calculatePredictedRating();
    return predictedRating <= 10; // It's impossible if the predicted rating exceeds the maximum of 10
  };
  
  // Calculate the current average rating and what the user needs
  const calculateCurrentAverageRating = () => {
    let enteredTotal = 0, enteredRatings = 0;
  
    curriculum.subjects.forEach((subject) => {
      const rating = parseFloat(ratings[subject.name]);
      if (rating) {
        enteredRatings += 1;
        enteredTotal += rating;
      }
    });
  
    return enteredRatings ? (enteredTotal / enteredRatings).toFixed(2) : 0;
  };
  

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!profile || !profile.name || !profile.email || !year || !career) {
      console.error("Missing profile, email, year or career information");
      return;
    }

    const userData = {
      name: profile.name,
      gmail: profile.email,
      year,
      career,
    };

    axios.post('http://localhost:8000/api/user/', userData)
      .then(res => {
        setCurriculum(res.data.curriculum);
        setIsReturningUser(true);
      })
      .catch(err => {
        console.error('Error:', err);
      });
  };

  // Generate data for the Line Chart (combined graph)
  const generateCombinedChartData = () => {
    if (!curriculum || !careerRequirement) return null;

    const labels = curriculum.subjects.map(subject => subject.name);
    const userGradesData = labels.map(subjectName => parseFloat(grades[subjectName]) || 0); // User's grades
    const predictedGrade = calculatePredictedGrade();
    const predictedGradesData = labels.map(subjectName => parseFloat(grades[subjectName]) || Math.min(predictedGrade, 4)); // Predicted grade for missing subjects (capped at 4)

    return {
      labels,
      datasets: [
        {
          label: 'User Grades',
          fill: false,
          lineTension: 0.1,
          backgroundColor: 'rgba(75,192,192,0.4)',
          borderColor: 'rgba(75,192,192,1)',
          data: userGradesData
        },
        {
          label: `${career} Predicted Grade`,
          fill: false,
          lineTension: 0.1,
          backgroundColor: 'rgba(153,102,255,0.4)',
          borderColor: 'rgba(153,102,255,1)',
          data: predictedGradesData
        }
      ]
    };
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <Nav />
      <div className="App">
        <h1>Register and Get Curriculum</h1>

        {profile && (
          <>
            <label>
              Name: {profile.name} ({profile.email})
            </label>

            {isReturningUser ? (
              <>
                <p>Year: {year}, Career Interest: {career}</p>
              </>
            ) : (
              <>
                <label>
                  Select Year:
                  <select value={year} onChange={(e) => setYear(Number(e.target.value))}>
                    <option value={1}>Year 1</option>
                    <option value={2}>Year 2</option>
                    <option value={3}>Year 3</option>
                    <option value={4}>Year 4</option>
                  </select>
                </label>

                <label>
                  Select Career Interest:
                  <select value={career} onChange={(e) => setCareer(e.target.value)}>
                    <option value="">Select a Career</option>
                    <option value="Data Analysis">Data Analysis</option>
                    <option value="Software Engineer">Software Engineer</option>
                    <option value="Web Development">Web Development</option>
                    <option value="Other">Other</option>
                  </select>
                </label>

                <button onClick={handleSubmit}>
                  Submit
                </button>
              </>
            )}
          </>
        )}

        {curriculum && (
          <div className="content-container">
            <div className="subject-list">
              <h2>Curriculum for Year {curriculum.year}</h2>
              <div className="subject-container">
                {curriculum.subjects && curriculum.subjects.map((subject, index) => (
                  <div key={index} className="subject-wrapper">
                    <div
                      className="subject-box"
                      onClick={() => handleSubjectClick(subject)}
                      style={{ cursor: 'pointer' }}
                    >
                      <h3>{subject.name}</h3>
                    </div>

                    {/* Popup for subject details and grade input */}
                    {showPopup && selectedSubject === subject && (
                      <div className="popup">
                        <div className="popup-content">
                          <h3>Subject Details</h3>
                          <p>Subject: {subject.name}</p>
                          <p>Description: {subject.description}</p>

                          {/* Grade input inside the popup */}
                          <div className="grade-input">
                            <h3>Your Grade:</h3>
                            <input
                              type="number"
                              min="0"
                              max="4"
                              step="0.01"
                              value={grades[subject.name] || ''}
                              onChange={(e) => handleGradeChange(subject.name, e.target.value)}
                            />
                          </div>

                          <button onClick={handleClosePopup}>Close</button>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>

            <div className="chart-container">
              <h2>User Grades vs {career} Predicted Grade</h2>
              {generateCombinedChartData() && (
                <Line
                  data={generateCombinedChartData()}
                  options={{
                    scales: {
                      y: {
                        beginAtZero: true,
                        max: 4
                      }
                    }
                  }}
                />
              )}
            </div>

            {/* Display current average and predicted grade required */}
            <div className="grade-info">
              <h2>Grade Information</h2>
              <p>Current Average Grade: {calculateCurrentAverage()}</p>
              {isPossibleToAchieveRequirement() ? (
                <p>To meet the requirement of {careerRequirement} average, you need a grade of <strong>{Math.min(calculatePredictedGrade(), 4)}</strong> in remaining subjects.</p>
              ) : (
                <p style={{ color: 'red' }}>It is impossible to meet the required average of {careerRequirement} based on your current grades. Please consider retaking some subjects to improve your grades.</p>
              )}
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default Course_2;
