import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Nav from '../Nav';
import { useSelector } from 'react-redux'; 
import StarRatingComponent from 'react-star-rating-component'; // Import star rating component
import './Course_2.css'; // Import the CSS file

function Course_2() {
  const [year, setYear] = useState(1);
  const [curriculum, setCurriculum] = useState(null);  
  const [isReturningUser, setIsReturningUser] = useState(false);  
  const [selectedSubject, setSelectedSubject] = useState(null);  
  const [showPopupIndex, setShowPopupIndex] = useState(null); // Track the index of the subject with an open popup
  const [ratings, setRatings] = useState({}); // Store user ratings for each subject

  const profile = useSelector(state => state.profile);

  useEffect(() => {
    if (profile) {
      axios.get(`http://localhost:8000/api/user/${profile.name}`)
        .then(res => {
          setCurriculum(res.data.curriculum);  
          setIsReturningUser(true);  
          // If user data contains ratings, initialize them
          const initialRatings = res.data.curriculum.subjects.reduce((acc, subject, index) => {
            acc[index] = subject.userRating || 0; // Initialize rating to 0 if not available
            return acc;
          }, {});
          setRatings(initialRatings);
        })
        .catch(err => {
          setIsReturningUser(false);  
        });
    }
  }, [profile]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const userData = { name: profile.name, year };

    axios.post('http://localhost:8000/api/user/', userData)
      .then(res => {
        setCurriculum(res.data.curriculum);  
      })
      .catch(err => {
        console.error('Error:', err);
      });
  };

  // Handle subject click to show popup below the subject
  const handleSubjectClick = (index) => {
    setShowPopupIndex(showPopupIndex === index ? null : index); // Toggle the popup visibility
  };

  const handleClosePopup = () => {
    setShowPopupIndex(null);  
  };

  // Handle rating change
  const onStarClick = (nextValue, prevValue, index) => {
    setRatings({ ...ratings, [index]: nextValue });
    // You could send the updated rating to your backend here
    axios.post(`http://localhost:8000/api/user/rating`, {
      name: profile.name,
      subjectIndex: index,
      rating: nextValue,
    }).catch(err => {
      console.error('Error:', err);
    });
  };

  return (
    <>
      <Nav />
      <div className="App">
        <h1>Register and Get Curriculum</h1>

        {profile && (
          <form onSubmit={handleSubmit}>
            <label>
              Name: {profile.name}
            </label>

            {!isReturningUser && (
              <>
                <label>
                  Select Year:
                  <select value={year} onChange={(e) => setYear(Number(e.target.value))}>
                    <option value={1}>Year 1</option>
                    <option value={2}>Year 2</option>
                    <option value={3}>Year 3</option>
                    <option value={4}>Year 4</option>
                  </select>
                </label>

                <button type="submit">Submit</button>
              </>
            )}
          </form>
        )}

        {/* Display curriculum */}
        {curriculum && (
          <div>
            <h2>Curriculum for Year {curriculum.year}</h2>
            <div className="subject-container">
              {curriculum.subjects.map((subject, index) => (
                <div key={index} className="subject-wrapper">
                  <div 
                    className="subject-box" 
                    onClick={() => handleSubjectClick(index)} 
                    style={{ cursor: 'pointer' }}
                  >
                    <h3>{subject.name}</h3>
                    {/* Star rating component */}
                    <StarRatingComponent 
                      name={`rating-${index}`} 
                      starCount={5}
                      value={ratings[index] || 0}
                      onStarClick={(nextValue, prevValue) => onStarClick(nextValue, prevValue, index)}
                    />
                  </div>

                  {/* Popup for subject details */}
                  {showPopupIndex === index && (
                    <div className="popup">
                      <div className="popup-content">
                        <h3>Subject Details</h3>
                        <p>Subject: {subject.name}</p>
                        <p>Description: {subject.description}</p>
                        <button onClick={handleClosePopup}>Close</button>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default Course_2;
