import React, { useEffect } from 'react';
import { GoogleLogin, GoogleLogout } from 'react-google-login';
import { gapi } from 'gapi-script';
import { useDispatch, useSelector } from 'react-redux';  
import Nav from '../Nav';

const Login = () => {
    const clientId = "989892277796-hr8ep4fu8ecrjn84gadef7655fpoucvb.apps.googleusercontent.com";
    const dispatch = useDispatch();
    const profile = useSelector(state => state.profile);

    // Initialize gapi client and check for existing profile in localStorage
    useEffect(() => {
        const storedProfile = localStorage.getItem('profile');
        if (storedProfile) {
            const parsedProfile = JSON.parse(storedProfile);
            dispatch({
                type: 'SET_PROFILE',
                payload: parsedProfile
            });
        }

        const initClient = async () => {
            try {
                await gapi.client.init({
                    clientId: clientId,
                    scope: ''
                });
            } catch (error) {
                console.error('Error initializing Google API client:', error);
            }
        };

        gapi.load("client:auth2", initClient);
    }, [dispatch, clientId]);

    // Success handler for Google login
    const onSuccess = (res) => {
        dispatch({
            type: 'SET_PROFILE',
            payload: res.profileObj
        });
        localStorage.setItem('profile', JSON.stringify(res.profileObj));
    };

    // Failure handler for Google login
    const onFailure = (res) => {
        console.error('Login failure:', res);
    };

    // Success handler for Google logout
    const logOut = () => {
        dispatch({ type: 'LOGOUT' });
        localStorage.removeItem('profile');
    };

    const handleImageError = (e) => {
        e.target.src = 'default-image-path.jpg'; // Replace this with your actual default image path
    };

    return (
        <div>
            <Nav />
            <h2>React Google Login</h2>
            <br />
            {profile ? (
                <>
                    <img 
                        src={profile.imageUrl} 
                        alt="User profile" 
                        onError={handleImageError}
                        style={{ width: '100px', height: '100px', borderRadius: '50%' }}
                    />
                    <h3>User Logged In</h3>
                    <p>Name: {profile.name}</p>
                    <p>Email: {profile.email}</p>
                    <br />
                    <GoogleLogout
                        clientId={clientId}
                        buttonText="Log out"
                        onLogoutSuccess={logOut}
                        onFailure={() => console.error('Logout failed')}
                    />
                </>
            ) : (
                <GoogleLogin
                    clientId={clientId}
                    buttonText="Sign in with Google"
                    onSuccess={onSuccess}
                    onFailure={onFailure}
                    cookiePolicy={'single_host_origin'}
                    isSignedIn={true}  // Ensures user stays logged in across sessions
                />
            )}
        </div>
    );
};

export default Login;
