import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Nav from '../Nav';
import { useSelector } from 'react-redux';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto'; // Import Chart.js
import "./Course_2.css";

function Course_2() {
  const [year, setYear] = useState(1);
  const [career, setCareer] = useState('');
  const [curriculum, setCurriculum] = useState(null);
  const [isReturningUser, setIsReturningUser] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [ratings, setRatings] = useState({});
  
  const profile = useSelector(state => state.profile);

  useEffect(() => {
    if (profile && profile.email) {
      axios.get(`http://localhost:8000/api/user/${encodeURIComponent(profile.email)}`)
        .then(res => {
          setCurriculum(res.data.curriculum);
          setYear(res.data.user.year);
          setCareer(res.data.user.career || "No career selected");
          setIsReturningUser(true);

          if (res.data.user && res.data.user.ratings) {
            const updatedRatings = {};
            res.data.curriculum.subjects.forEach((subject) => {
              updatedRatings[subject.name] = res.data.user.ratings[subject.name] || 0;
            });
            setRatings(updatedRatings);
          }
          setIsLoading(false);
        })
        .catch(err => {
          console.error('Error fetching user data:', err);
          setIsReturningUser(false);
          setIsLoading(false);
        });
    } else {
      setIsLoading(false);
    }
  }, [profile]);

  const careerRatingMap = {
    'Data Analysis': { 'Math 101': 4, 'Physics 101': 3, 'Programming 101': 5 },
    'Software Engineer': { 'Math 101': 3, 'Physics 101': 3, 'Programming 101': 5 },
    'Web Development': { 'Math 101': 2, 'Physics 101': 2, 'Programming 101': 5 },
  };

  const generateChartData = () => {
    if (!curriculum) return null;

    const labels = curriculum.subjects.map(subject => subject.name);
    const userRatings = labels.map(subjectName => ratings[subjectName] || 0); // Get ratings for each subject, default to 0
    const careerRatings = labels.map(subjectName => (careerRatingMap[career] && careerRatingMap[career][subjectName]) || 0); // Get career-based ratings

    return {
      labels,
      datasets: [
        {
          label: 'User Ratings',
          backgroundColor: 'rgba(75,192,192,0.4)',
          borderColor: 'rgba(75,192,192,1)',
          data: userRatings,
          fill: false, // Don't fill the area under the line
        },
        {
          label: `${career} Career Ratings`,
          backgroundColor: 'rgba(255,99,132,0.4)',
          borderColor: 'rgba(255,99,132,1)',
          data: careerRatings,
          fill: false, // Don't fill the area under the line
        }
      ]
    };
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <Nav />
      <div className="App">
        <h1>Register and Get Curriculum</h1>

        {profile && (
          <>
            <label>
              Name: {profile.name} ({profile.email})
            </label>

            {isReturningUser ? (
              <p>Year: {year}, Career Interest: {career}</p>
            ) : (
              <>
                <label>
                  Select Year:
                  <select value={year} onChange={(e) => setYear(Number(e.target.value))}>
                    <option value={1}>Year 1</option>
                    <option value={2}>Year 2</option>
                    <option value={3}>Year 3</option>
                    <option value={4}>Year 4</option>
                  </select>
                </label>

                <label>
                  Select Career Interest:
                  <select value={career} onChange={(e) => setCareer(e.target.value)}>
                    <option value="">Select a Career</option>
                    <option value="Data Analysis">Data Analysis</option>
                    <option value="Software Engineer">Software Engineer</option>
                    <option value="Web Development">Web Development</option>
                    <option value="Other">Other</option>
                  </select>
                </label>
              </>
            )}
          </>
        )}

        {curriculum && (
          <div className="content-container">
            <div className="chart-container">
              <h3>User Rating and Career Comparison Chart</h3>
              {generateChartData() && generateChartData().datasets[0].data.some(value => value > 0) ? (
                <Line
                  data={generateChartData()}
                  options={{
                    scales: {
                      y: {
                        beginAtZero: true,
                        max: 5
                      }
                    }
                  }}
                  width={300}
                  height={200}
                />
              ) : (
                <p>No ratings available yet.</p>
              )}
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default Course_2;
