import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { Button, Avatar, Typography, Container } from '@mui/material';
import { fetchProfile } from './actions';  // Import your fetchProfile action
import Nav from '../Nav';
import './profile.css';

const Profile = () => {
  const profile = useSelector(state => state.profile);  // Get profile from Redux store
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    dispatch(fetchProfile());  // Fetch the profile data when the component mounts
  }, [dispatch]);

  const logOut = () => {
    dispatch({ type: 'LOGOUT' });
    navigate('/login');
  };

  return (
    <>
      <Nav />
      <Container maxWidth="sm" style={{ textAlign: 'center', marginTop: '50px' }}>
        {profile.name ? (  // Check if profile data is available
          <div>
            <Typography variant="h3" gutterBottom>Profile</Typography>
            <Avatar src={profile.imageUrl} alt={profile.name} sx={{ width: 150, height: 150, margin: '20px auto' }} />
            <Typography variant="h5">Name: {profile.name}</Typography>
            <Typography variant="h6">Email: {profile.email}</Typography>
            <Typography variant="h6">Career: {profile.career}</Typography> {/* Display Career */}
            <Typography variant="h6">Year: {profile.year}</Typography>     {/* Display Year */}
            <Button
              variant="contained"
              color="primary"
              sx={{ marginTop: 3 }}
              onClick={logOut}
            >
              Log out
            </Button>
          </div>
        ) : (
          <Typography variant="h6">No user logged in</Typography>
        )}
      </Container>
    </>
  );
};

export default Profile;
